package Arrays;

public class initialisationAndDeclaration {

	public static void main(String[] args) {
		// TODO Auto-generated method 
		int [] arr = new int[6]; 
		arr[1]=10;
		arr[4]=20;
		for(int i=0;i<arr.length;i++) {
			System.out.print(arr[i]+" ");
		}
		System.out.println();
		int arr1 [] = {1,2,3,4};
		System.out.println(arr1[3]);

	}

}
