package primitiveTypeConversions;

public class typeConversions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int integer=25;
		double val=integer;
		System.out.println(val);
		long l=35;
		System.out.println(l);
		integer=(int)l;
		System.out.println(integer);
		

	}

}
